<?phpclass PhotoFollowers extends CI_Model {	private $table = "photo_followers";	private $pk = "f_id";	private $date = null;	public function checkexist($field,$value,$field2=null,$value2=null){     $returnValue = false;     if ($field2 && $value2){     	$query = $this->db->query("SELECT count(*) as count FROM `$this->table` WHERE `$field` = '".$value."' AND  `$field2` = '".$value2."'");     }else {      $query = $this->db->query("SELECT count(*) as count FROM `$this->table` WHERE `$field` = '".$value."' ");     }        if ($query->num_rows() > 0){        foreach ($query->result() as $row)         {           $count =  $row->count;         }     }     if ($count > 0){        $returnValue = true;        }    return $returnValue;	}			public function getinfo($field1,$field2,$value, $field3=null, $value3=null){	      $v = "";	      if ($field3 && $value3){	      	$query = $this->db->query("SELECT $field1 as val FROM `$this->table` WHERE `$field2` = '".$value."' AND  `$field3` = '".$value3."'");	      }else {	        $query = $this->db->query("SELECT $field1 as val FROM `$this->table` WHERE `$field2` = '".$value."' ");	      }	      if ($query->num_rows() > 0){	        foreach ($query->result() as $row)	         {	           $v =  $row->val;	         }	     }	     	    return $v;	  }	    public function getinfobyid($field,$id){     $returnValue = "";      $query = $this->db->query("SELECT `$field` as val from $this->table where $this->pk=$id");      if ($query->num_rows() > 0){        foreach ($query->result() as $row)         {           $returnValue =  $row->val;         }       }      return $returnValue;    }	  function getbyattribute($key=null,$value=null,$key2=null, $value2=null){  	if (($key2) && ($value2)){  		 return  $this->db->query("SELECT * FROM $this->table where `$key` = '".$value."' AND `$key2` = '".$value2."'");  	}else if ($key && $value){  	  return  $this->db->query("SELECT * FROM $this->table where `$key` = '".$value."'");  	}else {  		return  $this->db->query("SELECT * FROM $this->table");  	}  }       function getcountbyattribute($key,$value,$key2=null,$value2=null){  	if ($key2 && $value2){  		  $query = $this->db->query("Select count(*) as total from $this->table where `$key` = '".$value."' AND `$key2` = '".$value2."'");  	}  else {   	   $query = $this->db->query("Select count(*) as total from $this->table where `$key` = '".$value."' ");  	}     if ($query->num_rows() > 0){        foreach ($query->result() as $row)         {           $total =  $row->total;         }      }      return $total;  }    function getfollow($key,$value,$key2=null,$value2=null){ 				if ($key2 && $value2){			  $query = $this->db->query("Select * from $this->table where `$key` = '".$value."' AND `$key2` = '".$value2."'");		}  else {		   $query = $this->db->query("Select * from $this->table where `$key` = '".$value."' ");		}				return $query;		    }    function recentfollow($key,$value){				$query = $this->db->query("Select * from $this->table where `$key` = '".$value."' ORDER BY f_id DESC LIMIT 0,3 ");				return $query;	    }    function update($id,$data){     $query = $this->db->query("Select * from `$this->table` where $this->pk = '$id'"); 	 if ($query->num_rows() > 0){	     $this->db->where($this->pk, $id);		 $this->db->update($this->table, $data);		 return $id;	 } else {	 	if ($this->date){	 	  $this->db->set($this->date, 'NOW()', FALSE);	 	}	 	$this->db->insert($this->table, $data);	 	return $this->db->insert_id();	 }   }    function delete($id){  	return $this->db->delete($this->table, array($this->pk => $id));   }    function getfollowed($id){  	return $this->db->query("Select * from `$this->table` where member_id = '$id' LIMIT 0,5");  }    /*function getfollowedall($id){  	return $this->db->query("Select * from `$this->table` where member_id = '$id'");  }*/    function getTopFollowedSites()  {  	return $this->db->query("Select domain, count(*) as followed FROM `$this->table`");  }     function getTopFollowedSitesLimit($limit)  {  	return $this->db->query("Select domain, count(*) as followed FROM `$this->table`");  }    function getfollowersCount($domain){  	$query =  $this->db->query("Select * from `$this->table` where domain = '$domain' LIMIT 0,5");	return $query->num_rows();  }   /* function getonefollowed($userid){  	if ($this->checkexist('member_id',$userid)===true){  	  $query =  $this->db->query("Select domain from `$this->table` where member_id = '$userid' order by f_id LIMIT 1");  	}else {  	  $query =  $this->db->query("Select domain from `$this->table` ORDER BY RAND() LIMIT 1");	  	}  	if ($query->num_rows() > 0){        foreach ($query->result() as $row)         {           $domain =  $row->domain;         }      }      return $domain;  }*/ }