<html><head>    <script type="text/javascript" src="http://code.jquery.com/jquery-1.7.1.js"></script>    <script type="text/javascript">        $(document).ready(function(){            var shownIds = new Array();            setInterval(function(){                $.get("http://www.beta.photostream.com/dashboard/test2", function(data){                    data = $.parseJSON(data);                    for(i = 0; i < data.length; i++){                        if($.inArray(data[i]["id"], shownIds) == -1){                            $("#log").append(" " + data[i]["message"] + "<br />");                            shownIds.push(data[i]["id"]);                        }                    }                });            }, 2000);        });    </script></head><body>    <div id="log">    </div></body></html>