<?  $update_data = array(); $cnt = 0;foreach($updates->result() as $row_up): 		$update_data[$cnt]['fname'] = $this->photousers->getinfobyid('firstname',$row_up->userid);		$update_data[$cnt]['lname'] = $this->photousers->getinfobyid('lastname',$row_up->userid);		$update_data[$cnt]['avatar'] = $this->photopics->getinfobyid('filename',$this->photousers->getinfobyid('avatar_id',$row_up->userid));		$update_data[$cnt]['username'] = $this->photousers->getinfobyid('username',$row_up->userid);		$update_data[$cnt]['message'] = $row_up->message;		$update_data[$cnt]['title'] = $this->photostream->getinfobyid('title',$row_up->stream_id);		$update_data[$cnt]['id'] = $row_up->update_id;		$update_data[$cnt]['pcount'] = $row_up->photo_count;		$update_data[$cnt]['stream_id'] = $row_up->stream_id;				$cnt++;			endforeach;		$update_data['count'] = $cnt;	$update_data['num_rows'] = $updates->num_rows();		echo json_encode($update_data);	?>