<div class="wrap-newsFeed">					<div id="newsFeed">        				<ul id="newsFeed-ul" class="inline">							<? foreach($updates->result() as $row_up): ?>							<? $names = $this->photousers->getinfobyid('firstname',$row_up->userid)." ".$this->photousers->getinfobyid('lastname',$row_up->userid);?>							<li>								<div class="media">									<a class="pull-left" href="">										<img style="width:30px;height:30px;" src="<? echo $this->photopics->getinfobyid('filename',$this->photousers->getinfobyid('avatar_id',$row_up->userid));?>" alt="" />									</a>									<div class="media-body">										<a class="a-feed" href="/<?=$this->photousers->getinfobyid('username',$row_up->userid)?>">											<b><?=$names?></b> 											<span class="info-feed"><?  $messages = explode(" ",$row_up->message); ?><?=$messages[0]?> <b><?=$row_up->photo_count?> <?																							if($row_up->photo_count == 1){													echo "photo";												}else{													echo "photos";												}																																	?></b> <?=$messages[2]?> <?=$messages[3]?> in Album <a href="/stream/album/<?=url_title($this->photostream->getinfobyid('title',$row_up->stream_id))?>/<?=$row_up->stream_id?>"><?=$this->photostream->getinfobyid('title',$row_up->stream_id)?></a></span>										</a>									</div>								</div>							</li>							<? endforeach; ?>						</ul>					</div>				</div>				<script type="text/javascript" src="<?=base_url();?>js/jquery-slimscroll.js"></script>				<script type="text/javascript">				    $(function(){				      $('#newsFeed').slimScroll({				          height: 'auto'				      });				    });				</script>