<?php $this->load->view('backend/header')?><?php $this->load->view('backend/navigation')?><link href="/css/pages/dashboard.css" rel="stylesheet"><link href="<?=base_url()?>css/photostream.css" rel="stylesheet"> <script src="<?=base_url()?>js/blocksit.min.js"></script><div class="main">    <div class="container">      <div class="row">      		<div class="span12">      		      		      		<div class="widget stacked ">      			      			<div class="widget-header">      				<i class="icon-pencil"></i>      				<h3>Import from Twitter</h3>  				</div> <!-- /widget-header -->								<div class="widget-content">					<center>						<div id="connect_form">							<input type="text" id="twitter_username" placeholder="your twitter username"/>						</div>					</center>																<div id="twitter_result">																												</div>																		</div><!-- widget content -->							</div><!-- widget stacked -->		</div><!-- span12 -->		      </div> <!-- /row -->    </div> <!-- /container -->    </div> <!-- /main --><script type="text/javascript">	$(document).ready(function(){				$('#twitter_result').BlocksIt({		   numOfCol: 5,		   offsetX: 8,		   offsetY: 8		});				$('#connect_form input').bind('keypress', function(e) {				var code = (e.keyCode ? e.keyCode : e.which);				 				if(code == 13) { //Enter keycode					$('#twitter_result').html('<center>Fetching your twitter media gallery<br><div class="progress progress-primary progress-striped active"><div class="bar" style="width: 65%"></div></div></center>');					var twitter_username = $('#twitter_username').val();					$.post('twitter/getGalleryContents',{twitter_username:twitter_username},function(html_data){						$('#twitter_result').html(html_data);					});				}		});	});	</script><?php $this->load->view('backend/footer')?> 