<style>	#main_form_options{		background: none repeat scroll 0 0 #FFFFFF;		border-radius: 10px;		color: #000000;		float: left;		font-family: 'Open Sans',sans-serif;		height: 435px;		padding: 5px 10px;		text-align: left;		width: 315px;	}	table p{margin:0px;}	input[type="radio"]{margin:10px;}	#main_form_options h2, #main_form_options h3, #main_form_options p{		color: #5B6A71;		font-weight: normal;	}	#main_form_options h2{		font-size: 20px;		line-height: 28px;		margin-bottom: 8px;		text-align: center;	}	#main_form_options h3{		font-size: 18px;		line-height: 26px;	}	.btn-primary {		background-color: #006DCC;		background-image: linear-gradient(to bottom, #0088CC, #0044CC);		background-repeat: repeat-x;		border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);		color: #FFFFFF;		text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);	}	.btn-mini {		border-radius: 3px;		font-size: 10.5px;		padding: 0 6px;	}</style><div class="contact_us_div">	<div id="main_form_options">		<h2>Hello from <?echo ucfirst($domain)?></h2>		<p style="font-size: 12px;text-align:center;">Please choose any of the options that interests you.</p>		<br>		<h3> &nbsp; &nbsp; I&lsquo;M INTERESTED IN:</h3>		<table>			<tr >				<td><input type="radio" name="radio_form" id="partner" value="partner_form"/></td>				<td><p>Partner / Develop with Javapoint.com</p></td>			</tr>			<tr  >				<td><input type="radio" name="radio_form" id="staffing" value="staffing_form"/></td>				<td><p>Staffing Opportunities with Javapoint.com</p></td>			</tr>			<tr  >				<td><input type="radio" name="radio_form" id="offer" value="offer_form"/></td>				<td><p>Submitting an Offer for Javapoint.com</p></td>			</tr>			<tr  >				<td><input type="radio" name="radio_form" id="inquire" value="inquire_form"/></td>				<td><p>Inquire / Sponsor with Javapoint.com</p></td>			</tr>		</table>	</div><!-- main_form_options -->	<div id="form_container">		<p style="margin: 20px 40px 10px 0;text-align: right;"><button class="btn btn-primary btn-mini" id="back">Back</button></p>		<center id="loadingforms" style="margin-top: -40px;">			<img src="https://www.roc.com/sites/prod-roc-com.jnj.acquia.adyax.com/files/images/country-loader.gif">		</center>		<div id="show_partner" class="form_include"></div>		<div id="show_staffing" class="form_include"></div>		<div id="show_offer" class="form_include"></div>		<div id="show_inquire" class="form_include"></div>	</div><!-- form_container --></div><!-- contact_us_div --><script type="text/javascript">	$(document).ready(function(){		$('#form_container').hide();		$('#show_partner').hide();		$('#show_staffing').hide();		$('#show_offer').hide();		$('#show_inquire').hide();		$('input[type="radio"][name="radio_form"]').change(function() {			$('#main_form_options').hide();			var id = $(this).attr('id');			var to_show_div = "show_"+id;						$('#show_partner').html('');			$('#show_staffing').html('');			$('#show_offer').html('');			$('#show_inquire').html('');						$('#loadingforms').show();						if(id=='partner'){				$("#show_partner").load("serviceforms/service_partner.php");							}else if(id=='staffing'){				$("#show_staffing").load("serviceforms/service_staffing.php");			}else if(id=='offer'){				$("#show_offer").load("serviceforms/service_offer.php");			}else if(id=='inquire'){				$("#show_inquire").load("serviceforms/service_inquire.php");			}						$("#"+to_show_div).show();			$('#form_container').show();		});		$('button#back').click(function(){			$('.form_include').hide();			$('#form_container').hide();			$('#main_form_options').show("slideUp");		});	});</script>